import React, { useEffect, useState } from 'react'

const CursorFollower = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 })
  const [trail, setTrail] = useState([])

  useEffect(() => {
    const handleMouseMove = (e) => {
      const newPosition = { x: e.clientX, y: e.clientY }
      setMousePosition(newPosition)
      
      // Add to trail
      setTrail(prev => {
        const newTrail = [...prev, { ...newPosition, id: Date.now() }]
        // Keep only last 15 positions
        return newTrail.slice(-15)
      })
    }

    window.addEventListener('mousemove', handleMouseMove)

    return () => {
      window.removeEventListener('mousemove', handleMouseMove)
    }
  }, [])

  return (
    <>
      {/* Main cursor dot */}
      <div
        className="fixed pointer-events-none z-50 transition-transform duration-100 ease-out"
        style={{
          left: mousePosition.x,
          top: mousePosition.y,
          width: '6px',
          height: '6px',
          backgroundColor: '#667eea',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          boxShadow: '0 0 10px rgba(102, 126, 234, 0.8)',
          mixBlendMode: 'screen'
        }}
      />
      
      {/* Large border circle */}
      <div
        className="fixed pointer-events-none z-40 transition-transform duration-200 ease-out"
        style={{
          left: mousePosition.x,
          top: mousePosition.y,
          width: '50px',
          height: '50px',
          border: '2px solid rgba(102, 126, 234, 0.4)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          backdropFilter: 'blur(2px)'
        }}
      />
      
      {/* Beautiful trailing dots */}
      {trail.map((pos, index) => {
        const opacity = (index / trail.length) * 0.6
        const scale = 1 - (index / trail.length) * 0.8
        const delay = index * 20
        
        return (
          <div
            key={pos.id}
            className="fixed pointer-events-none z-30 transition-all duration-300 ease-out"
            style={{
              left: pos.x,
              top: pos.y,
              width: '4px',
              height: '4px',
              backgroundColor: `rgba(102, 126, 234, ${opacity})`,
              borderRadius: '50%',
              transform: `translate(-50%, -50%) scale(${scale})`,
              transitionDelay: `${delay}ms`,
              boxShadow: `0 0 ${8 - index}px rgba(102, 126, 234, ${opacity * 0.5})`
            }}
          />
        )
      })}
      
      {/* Glowing trail effect */}
      {trail.slice(-8).map((pos, index) => {
        const opacity = (index / 8) * 0.3
        const size = 20 - index * 2
        
        return (
          <div
            key={`glow-${pos.id}`}
            className="fixed pointer-events-none z-20 transition-all duration-500 ease-out"
            style={{
              left: pos.x,
              top: pos.y,
              width: `${size}px`,
              height: `${size}px`,
              background: `radial-gradient(circle, rgba(102, 126, 234, ${opacity}) 0%, transparent 70%)`,
              borderRadius: '50%',
              transform: 'translate(-50%, -50%)',
              transitionDelay: `${index * 30}ms`
            }}
          />
        )
      })}
      
      {/* Sparkle effect */}
      {trail.slice(-5).map((pos, index) => {
        if (index % 2 === 0) return null
        
        return (
          <div
            key={`sparkle-${pos.id}`}
            className="fixed pointer-events-none z-35 animate-pulse"
            style={{
              left: pos.x + (Math.random() - 0.5) * 20,
              top: pos.y + (Math.random() - 0.5) * 20,
              width: '2px',
              height: '2px',
              backgroundColor: 'rgba(255, 255, 255, 0.8)',
              borderRadius: '50%',
              transform: 'translate(-50%, -50%)',
              animationDelay: `${index * 100}ms`
            }}
          />
        )
      })}
    </>
  )
}

export default CursorFollower
