import React, { useEffect, useState } from 'react'
import { motion, AnimatePresence } from 'framer-motion'

const Confetti = ({ isActive, onComplete }) => {
  const [particles, setParticles] = useState([])

  useEffect(() => {
    if (isActive) {
      // Create confetti particles
      const newParticles = []
      const colors = [
        '#667eea', '#764ba2', '#f093fb', '#f5576c', 
        '#4facfe', '#00f2fe', '#43e97b', '#38f9d7',
        '#fa709a', '#fee140', '#a8edea', '#fed6e3'
      ]
      
      for (let i = 0; i < 120; i++) {
        newParticles.push({
          id: i,
          x: Math.random() * window.innerWidth,
          y: -30,
          vx: (Math.random() - 0.5) * 6,
          vy: Math.random() * 2 + 1,
          rotation: Math.random() * 360,
          rotationSpeed: (Math.random() - 0.5) * 8,
          color: colors[Math.floor(Math.random() * colors.length)],
          size: Math.random() * 6 + 3,
          opacity: 1,
          type: Math.random() > 0.5 ? 'circle' : 'square',
          delay: Math.random() * 0.5
        })
      }
      
      setParticles(newParticles)
      
      // Auto-complete after animation
      setTimeout(() => {
        onComplete && onComplete()
      }, 2500)
    }
  }, [isActive, onComplete])

  useEffect(() => {
    if (!isActive || particles.length === 0) return

    const interval = setInterval(() => {
      setParticles(prev => 
        prev.map(particle => ({
          ...particle,
          x: particle.x + particle.vx,
          y: particle.y + particle.vy,
          vy: particle.vy + 0.08, // gravity
          rotation: particle.rotation + particle.rotationSpeed,
          opacity: particle.opacity > 0 ? particle.opacity - 0.008 : 0
        })).filter(particle => particle.opacity > 0)
      )
    }, 16) // 60fps

    return () => clearInterval(interval)
  }, [isActive, particles])

  if (!isActive) return null

  return (
    <div className="fixed inset-0 pointer-events-none z-50">
      <AnimatePresence>
        {particles.map(particle => (
          <motion.div
            key={particle.id}
            className="absolute"
            initial={{ 
              opacity: 0, 
              scale: 0,
              y: particle.y - 50
            }}
            animate={{ 
              opacity: particle.opacity,
              scale: 1,
              y: particle.y,
              x: particle.x,
              rotate: particle.rotation
            }}
            exit={{ 
              opacity: 0, 
              scale: 0,
              y: particle.y + 100
            }}
            transition={{ 
              duration: 0.6,
              ease: [0.25, 0.46, 0.45, 0.94],
              delay: particle.delay
            }}
            style={{
              width: particle.size,
              height: particle.size,
              backgroundColor: particle.color,
              borderRadius: particle.type === 'circle' ? '50%' : '2px',
              boxShadow: `0 0 ${particle.size * 1.5}px ${particle.color}`,
              willChange: 'transform'
            }}
          />
        ))}
      </AnimatePresence>
    </div>
  )
}

export default Confetti
