import React, { useEffect, useState, useRef, useCallback } from 'react'
import { useSpring, animated, config } from '@react-spring/web'
import { gsap } from 'gsap'

const CursorFollower = () => {
  const [trail, setTrail] = useState([])
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 })
  const canvasRef = useRef(null)
  const lastMousePosition = useRef({ x: 0, y: 0 })
  const velocity = useRef({ x: 0, y: 0 })
  const cursorRef = useRef(null)
  const borderRef = useRef(null)
  const magneticRef = useRef(null)

  // React Spring for ultra-smooth animations
  const [cursorSpring, cursorApi] = useSpring(() => ({
    x: 0,
    y: 0,
    config: { mass: 1, tension: 1200, friction: 40 }
  }))

  const [borderSpring, borderApi] = useSpring(() => ({
    x: 0,
    y: 0,
    config: { mass: 1.2, tension: 800, friction: 35 }
  }))

  const [magneticSpring, magneticApi] = useSpring(() => ({
    x: 0,
    y: 0,
    scale: 1,
    config: { mass: 1.5, tension: 600, friction: 30 }
  }))

  // GSAP for trail particles
  const createTrailParticle = useCallback((x, y, index) => {
    const particle = document.createElement('div')
    particle.className = 'trail-particle'
    particle.style.cssText = `
      position: fixed;
      width: ${8 - index * 0.3}px;
      height: ${8 - index * 0.3}px;
      background: radial-gradient(circle, rgba(102, 126, 234, ${0.9 - index * 0.05}) 0%, rgba(118, 75, 162, ${0.6 - index * 0.03}) 100%);
      border-radius: 50%;
      pointer-events: none;
      z-index: 35;
      transform: translate(-50%, -50%);
      box-shadow: 0 0 ${15 - index}px rgba(102, 126, 234, ${0.8 - index * 0.04});
    `
    document.body.appendChild(particle)

    gsap.fromTo(particle, 
      { 
        x: x, 
        y: y, 
        scale: 1,
        opacity: 0.9 - index * 0.05
      },
      { 
        x: x + (Math.random() - 0.5) * 20,
        y: y + (Math.random() - 0.5) * 20,
        scale: 0.3,
        opacity: 0,
        duration: 0.8,
        ease: "power2.out",
        delay: index * 0.02,
        onComplete: () => {
          document.body.removeChild(particle)
        }
      }
    )
  }, [])

  // Smooth mouse tracking with velocity
  const updateMousePosition = useCallback((x, y) => {
    const now = Date.now()
    const deltaTime = now - (lastMousePosition.current.timestamp || now)
    
    if (deltaTime > 0) {
      const vx = (x - lastMousePosition.current.x) / deltaTime * 16
      const vy = (y - lastMousePosition.current.y) / deltaTime * 16
      
      velocity.current.x = vx
      velocity.current.y = vy
      
      // Update magnetic field scale based on velocity
      const speed = Math.sqrt(vx * vx + vy * vy)
      const scale = 1 + Math.min(speed / 50, 0.5)
      magneticApi.start({ scale })
    }
    
    lastMousePosition.current = { x, y, timestamp: now }
    setMousePosition({ x, y })
    
    // Update springs
    cursorApi.start({ x, y })
    borderApi.start({ x, y })
    magneticApi.start({ x, y })
  }, [cursorApi, borderApi, magneticApi])

  useEffect(() => {
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    
    // Set canvas size with device pixel ratio for crisp rendering
    const resizeCanvas = () => {
      const dpr = window.devicePixelRatio || 1
      const rect = canvas.getBoundingClientRect()
      
      canvas.width = rect.width * dpr
      canvas.height = rect.height * dpr
      canvas.style.width = rect.width + 'px'
      canvas.style.height = rect.height + 'px'
      
      ctx.scale(dpr, dpr)
    }
    
    resizeCanvas()
    window.addEventListener('resize', resizeCanvas)

    // Ultra-optimized mouse move handler with GSAP
    let rafId = null
    const handleMouseMove = (e) => {
      if (rafId) return
      
      rafId = requestAnimationFrame(() => {
        updateMousePosition(e.clientX, e.clientY)
        
        // Create trail particles with GSAP
        for (let i = 0; i < 5; i++) {
          createTrailParticle(e.clientX, e.clientY, i)
        }
        
        // Add to trail state for React components
        setTrail(prev => {
          const newTrail = [...prev, { 
            x: e.clientX, 
            y: e.clientY, 
            id: Date.now(),
            velocity: Math.sqrt(velocity.current.x ** 2 + velocity.current.y ** 2)
          }]
          return newTrail.slice(-15)
        })
        
        rafId = null
      })
    }

    window.addEventListener('mousemove', handleMouseMove, { passive: true })

    return () => {
      window.removeEventListener('mousemove', handleMouseMove)
      window.removeEventListener('resize', resizeCanvas)
      if (rafId) {
        cancelAnimationFrame(rafId)
      }
    }
  }, [updateMousePosition, createTrailParticle])

  return (
    <>
      {/* High-performance canvas for ripple effects */}
      <canvas
        ref={canvasRef}
        className="fixed top-0 left-0 w-full h-full pointer-events-none z-10"
        style={{ 
          background: 'transparent',
          willChange: 'transform'
        }}
      />
      
      {/* Ultra-smooth main cursor dot with React Spring */}
      <animated.div
        ref={cursorRef}
        className="fixed pointer-events-none z-50"
        style={{
          ...cursorSpring,
          width: '12px',
          height: '12px',
          background: 'radial-gradient(circle, #667eea 0%, #764ba2 50%, rgba(102, 126, 234, 0.8) 100%)',
          borderRadius: '50%',
          transform: cursorSpring.x.to(x => cursorSpring.y.to(y => `translate(${x}px, ${y}px) translate(-50%, -50%)`)),
          boxShadow: `
            0 0 20px rgba(102, 126, 234, 0.8),
            0 0 40px rgba(102, 126, 234, 0.4),
            0 0 60px rgba(102, 126, 234, 0.2)
          `,
          mixBlendMode: 'screen',
          willChange: 'transform'
        }}
      />
      
      {/* Smooth border circle with React Spring */}
      <animated.div
        ref={borderRef}
        className="fixed pointer-events-none z-40"
        style={{
          ...borderSpring,
          width: '50px',
          height: '50px',
          border: '2px solid rgba(102, 126, 234, 0.7)',
          borderRadius: '50%',
          transform: borderSpring.x.to(x => borderSpring.y.to(y => `translate(${x}px, ${y}px) translate(-50%, -50%)`)),
          backdropFilter: 'blur(4px)',
          boxShadow: '0 0 40px rgba(102, 126, 234, 0.4)',
          willChange: 'transform'
        }}
      />
      
      {/* Magnetic field with velocity-based scaling */}
      <animated.div
        ref={magneticRef}
        className="fixed pointer-events-none z-30"
        style={{
          ...magneticSpring,
          width: '220px',
          height: '220px',
          background: 'radial-gradient(circle, rgba(102, 126, 234, 0.12) 0%, rgba(102, 126, 234, 0.06) 50%, transparent 70%)',
          borderRadius: '50%',
          transform: magneticSpring.x.to(x => 
            magneticSpring.y.to(y => 
              magneticSpring.scale.to(scale => 
                `translate(${x}px, ${y}px) translate(-50%, -50%) scale(${scale})`
              )
            )
          ),
          filter: 'blur(12px)',
          willChange: 'transform'
        }}
      />
      
      {/* Dynamic background distortion */}
      <animated.div
        className="fixed pointer-events-none z-15"
        style={{
          ...magneticSpring,
          width: '350px',
          height: '350px',
          background: `radial-gradient(circle, 
            rgba(102, 126, 234, 0.06) 0%, 
            rgba(118, 75, 162, 0.04) 50%, 
            transparent 80%
          )`,
          borderRadius: '50%',
          transform: magneticSpring.x.to(x => 
            magneticSpring.y.to(y => 
              magneticSpring.scale.to(scale => 
                `translate(${x}px, ${y}px) translate(-50%, -50%) scale(${scale * 1.2})`
              )
            )
          ),
          filter: 'blur(25px)',
          mixBlendMode: 'overlay',
          willChange: 'transform'
        }}
      />
    </>
  )
}

export default CursorFollower
