import React, { useEffect, useRef } from 'react'

const CursorFollower = () => {
  const cursorRef = useRef(null)
  const borderRef = useRef(null)

  useEffect(() => {
    const cursor = cursorRef.current
    const border = borderRef.current

    let mouseX = 0
    let mouseY = 0
    let borderX = 0
    let borderY = 0

    const handleMouseMove = (e) => {
      mouseX = e.clientX
      mouseY = e.clientY
    }

    // Animation loop for smooth border following
    const animate = () => {
      // Main cursor follows instantly
      cursor.style.left = mouseX + 'px'
      cursor.style.top = mouseY + 'px'
      
      // Border circle follows with delay (trail effect)
      borderX += (mouseX - borderX) * 0.1
      borderY += (mouseY - borderY) * 0.1
      
      border.style.left = borderX + 'px'
      border.style.top = borderY + 'px'
      
      requestAnimationFrame(animate)
    }

    window.addEventListener('mousemove', handleMouseMove)
    animate()

    return () => {
      window.removeEventListener('mousemove', handleMouseMove)
    }
  }, [])

  return (
    <>
      {/* Simple main cursor dot */}
      <div
        ref={cursorRef}
        className="fixed pointer-events-none z-50"
        style={{
          width: '8px',
          height: '8px',
          background: '#667eea',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          boxShadow: '0 0 10px rgba(102, 126, 234, 0.5)',
          willChange: 'transform'
        }}
      />
      
      {/* Border circle with trail effect */}
      <div
        ref={borderRef}
        className="fixed pointer-events-none z-40"
        style={{
          width: '40px',
          height: '40px',
          border: '2px solid rgba(102, 126, 234, 0.6)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          willChange: 'transform'
        }}
      />
    </>
  )
}

export default CursorFollower
