import React, { useEffect, useState, useRef } from 'react'

const CursorFollower = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 })
  const [trail, setTrail] = useState([])
  const canvasRef = useRef(null)

  useEffect(() => {
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    
    // Set canvas size
    const resizeCanvas = () => {
      canvas.width = window.innerWidth
      canvas.height = window.innerHeight
    }
    
    resizeCanvas()
    window.addEventListener('resize', resizeCanvas)

    const handleMouseMove = (e) => {
      const newPosition = { x: e.clientX, y: e.clientY }
      setMousePosition(newPosition)
      
      // Add to trail
      setTrail(prev => {
        const newTrail = [...prev, { ...newPosition, id: Date.now() }]
        return newTrail.slice(-20)
      })

      // Create ripple effect on canvas
      createRipple(e.clientX, e.clientY)
    }

    const createRipple = (x, y) => {
      let radius = 0
      const maxRadius = 100
      const ripple = () => {
        ctx.clearRect(0, 0, canvas.width, canvas.height)
        
        // Draw existing ripples
        ctx.globalCompositeOperation = 'source-over'
        ctx.fillStyle = 'rgba(102, 126, 234, 0.1)'
        ctx.beginPath()
        ctx.arc(x, y, radius, 0, Math.PI * 2)
        ctx.fill()
        
        // Draw glow effect
        const gradient = ctx.createRadialGradient(x, y, 0, x, y, radius)
        gradient.addColorStop(0, 'rgba(102, 126, 234, 0.3)')
        gradient.addColorStop(0.5, 'rgba(102, 126, 234, 0.1)')
        gradient.addColorStop(1, 'rgba(102, 126, 234, 0)')
        
        ctx.fillStyle = gradient
        ctx.beginPath()
        ctx.arc(x, y, radius, 0, Math.PI * 2)
        ctx.fill()
        
        radius += 2
        
        if (radius < maxRadius) {
          requestAnimationFrame(ripple)
        } else {
          // Fade out
          setTimeout(() => {
            ctx.clearRect(0, 0, canvas.width, canvas.height)
          }, 1000)
        }
      }
      
      ripple()
    }

    window.addEventListener('mousemove', handleMouseMove)

    return () => {
      window.removeEventListener('mousemove', handleMouseMove)
      window.removeEventListener('resize', resizeCanvas)
    }
  }, [])

  return (
    <>
      {/* Background canvas for ripple effects */}
      <canvas
        ref={canvasRef}
        className="fixed top-0 left-0 w-full h-full pointer-events-none z-10"
        style={{ background: 'transparent' }}
      />
      
      {/* Main cursor dot */}
      <div
        className="fixed pointer-events-none z-50 transition-transform duration-100 ease-out"
        style={{
          left: mousePosition.x,
          top: mousePosition.y,
          width: '8px',
          height: '8px',
          background: 'radial-gradient(circle, #667eea 0%, #764ba2 100%)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          boxShadow: '0 0 20px rgba(102, 126, 234, 0.8), 0 0 40px rgba(102, 126, 234, 0.4)',
          mixBlendMode: 'screen'
        }}
      />
      
      {/* Large border circle */}
      <div
        className="fixed pointer-events-none z-40 transition-transform duration-200 ease-out"
        style={{
          left: mousePosition.x,
          top: mousePosition.y,
          width: '60px',
          height: '60px',
          border: '2px solid rgba(102, 126, 234, 0.6)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          backdropFilter: 'blur(3px)',
          boxShadow: '0 0 30px rgba(102, 126, 234, 0.3)'
        }}
      />
      
      {/* Magnetic field effect */}
      <div
        className="fixed pointer-events-none z-30 transition-all duration-500 ease-out"
        style={{
          left: mousePosition.x,
          top: mousePosition.y,
          width: '200px',
          height: '200px',
          background: 'radial-gradient(circle, rgba(102, 126, 234, 0.1) 0%, rgba(102, 126, 234, 0.05) 50%, transparent 70%)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          filter: 'blur(10px)'
        }}
      />
      
      {/* Beautiful trailing dots with glow */}
      {trail.map((pos, index) => {
        const opacity = (index / trail.length) * 0.8
        const scale = 1 - (index / trail.length) * 0.6
        const delay = index * 15
        const size = 6 - (index / trail.length) * 3
        
        return (
          <div
            key={pos.id}
            className="fixed pointer-events-none z-35 transition-all duration-400 ease-out"
            style={{
              left: pos.x,
              top: pos.y,
              width: `${size}px`,
              height: `${size}px`,
              background: `radial-gradient(circle, rgba(102, 126, 234, ${opacity}) 0%, rgba(118, 75, 162, ${opacity * 0.5}) 100%)`,
              borderRadius: '50%',
              transform: `translate(-50%, -50%) scale(${scale})`,
              transitionDelay: `${delay}ms`,
              boxShadow: `0 0 ${15 - index}px rgba(102, 126, 234, ${opacity * 0.8}), 0 0 ${30 - index * 2}px rgba(102, 126, 234, ${opacity * 0.4})`
            }}
          />
        )
      })}
      
      {/* Energy particles */}
      {trail.slice(-10).map((pos, index) => {
        if (index % 3 !== 0) return null
        
        const angle = (index * 36) * (Math.PI / 180)
        const distance = 30 + index * 5
        const particleX = pos.x + Math.cos(angle) * distance
        const particleY = pos.y + Math.sin(angle) * distance
        
        return (
          <div
            key={`particle-${pos.id}-${index}`}
            className="fixed pointer-events-none z-25 sparkle-animation"
            style={{
              left: particleX,
              top: particleY,
              width: '3px',
              height: '3px',
              background: 'radial-gradient(circle, rgba(255, 255, 255, 0.9) 0%, rgba(102, 126, 234, 0.6) 100%)',
              borderRadius: '50%',
              transform: 'translate(-50%, -50%)',
              animationDelay: `${index * 50}ms`,
              animationDuration: '1.5s'
            }}
          />
        )
      })}
      
      {/* Background distortion effect */}
      <div
        className="fixed pointer-events-none z-15 transition-all duration-300 ease-out"
        style={{
          left: mousePosition.x,
          top: mousePosition.y,
          width: '300px',
          height: '300px',
          background: 'radial-gradient(circle, rgba(102, 126, 234, 0.05) 0%, rgba(118, 75, 162, 0.03) 50%, transparent 80%)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          filter: 'blur(20px)',
          mixBlendMode: 'overlay'
        }}
      />
    </>
  )
}

export default CursorFollower
