import React, { useEffect, useState, useRef, useCallback } from 'react'

const CursorFollower = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 })
  const [trail, setTrail] = useState([])
  const canvasRef = useRef(null)
  const animationRef = useRef(null)
  const lastMousePosition = useRef({ x: 0, y: 0 })
  const velocity = useRef({ x: 0, y: 0 })

  // Smooth mouse tracking with velocity
  const updateMousePosition = useCallback((x, y) => {
    const now = Date.now()
    const deltaTime = now - (lastMousePosition.current.timestamp || now)
    
    if (deltaTime > 0) {
      velocity.current.x = (x - lastMousePosition.current.x) / deltaTime * 16 // 60fps
      velocity.current.y = (y - lastMousePosition.current.y) / deltaTime * 16
    }
    
    lastMousePosition.current = { x, y, timestamp: now }
    setMousePosition({ x, y })
  }, [])

  useEffect(() => {
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    
    // Set canvas size with device pixel ratio for crisp rendering
    const resizeCanvas = () => {
      const dpr = window.devicePixelRatio || 1
      const rect = canvas.getBoundingClientRect()
      
      canvas.width = rect.width * dpr
      canvas.height = rect.height * dpr
      canvas.style.width = rect.width + 'px'
      canvas.style.height = rect.height + 'px'
      
      ctx.scale(dpr, dpr)
    }
    
    resizeCanvas()
    window.addEventListener('resize', resizeCanvas)

    // Throttled mouse move handler for smooth performance
    let ticking = false
    const handleMouseMove = (e) => {
      if (!ticking) {
        requestAnimationFrame(() => {
          updateMousePosition(e.clientX, e.clientY)
          
          // Add to trail with smooth interpolation
          setTrail(prev => {
            const newTrail = [...prev, { 
              x: e.clientX, 
              y: e.clientY, 
              id: Date.now(),
              velocity: Math.sqrt(velocity.current.x ** 2 + velocity.current.y ** 2)
            }]
            return newTrail.slice(-25) // Keep more trail points for smoother effect
          })
          
          ticking = false
        })
        ticking = true
      }
    }

    // Create smooth ripple effect
    const createRipple = (x, y, intensity = 1) => {
      let radius = 0
      const maxRadius = 80 * intensity
      const ripple = () => {
        ctx.clearRect(0, 0, canvas.width, canvas.height)
        
        // Draw multiple ripple layers for depth
        for (let i = 0; i < 3; i++) {
          const layerRadius = radius - i * 10
          if (layerRadius > 0) {
            const opacity = (1 - layerRadius / maxRadius) * 0.15 * intensity
            const gradient = ctx.createRadialGradient(x, y, 0, x, y, layerRadius)
            gradient.addColorStop(0, `rgba(102, 126, 234, ${opacity})`)
            gradient.addColorStop(0.7, `rgba(118, 75, 162, ${opacity * 0.5})`)
            gradient.addColorStop(1, 'rgba(102, 126, 234, 0)')
            
            ctx.fillStyle = gradient
            ctx.beginPath()
            ctx.arc(x, y, layerRadius, 0, Math.PI * 2)
            ctx.fill()
          }
        }
        
        radius += 1.5
        
        if (radius < maxRadius) {
          animationRef.current = requestAnimationFrame(ripple)
        } else {
          setTimeout(() => {
            ctx.clearRect(0, 0, canvas.width, canvas.height)
          }, 500)
        }
      }
      
      ripple()
    }

    window.addEventListener('mousemove', handleMouseMove, { passive: true })

    return () => {
      window.removeEventListener('mousemove', handleMouseMove)
      window.removeEventListener('resize', resizeCanvas)
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current)
      }
    }
  }, [updateMousePosition])

  // Calculate smooth trail positions with easing
  const getTrailPosition = (index, total) => {
    const progress = index / total
    const easeOut = 1 - Math.pow(1 - progress, 3) // Cubic ease-out
    return easeOut
  }

  return (
    <>
      {/* High-performance canvas for ripple effects */}
      <canvas
        ref={canvasRef}
        className="fixed top-0 left-0 w-full h-full pointer-events-none z-10"
        style={{ 
          background: 'transparent',
          willChange: 'transform'
        }}
      />
      
      {/* Ultra-smooth main cursor dot */}
      <div
        className="fixed pointer-events-none z-50"
        style={{
          left: mousePosition.x,
          top: mousePosition.y,
          width: '10px',
          height: '10px',
          background: 'radial-gradient(circle, #667eea 0%, #764ba2 50%, rgba(102, 126, 234, 0.8) 100%)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          boxShadow: `
            0 0 20px rgba(102, 126, 234, 0.8),
            0 0 40px rgba(102, 126, 234, 0.4),
            0 0 60px rgba(102, 126, 234, 0.2)
          `,
          mixBlendMode: 'screen',
          willChange: 'transform',
          transition: 'none'
        }}
      />
      
      {/* Smooth border circle */}
      <div
        className="fixed pointer-events-none z-40"
        style={{
          left: mousePosition.x,
          top: mousePosition.y,
          width: '70px',
          height: '70px',
          border: '2px solid rgba(102, 126, 234, 0.7)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          backdropFilter: 'blur(4px)',
          boxShadow: '0 0 40px rgba(102, 126, 234, 0.4)',
          willChange: 'transform',
          transition: 'none'
        }}
      />
      
      {/* Magnetic field with velocity-based scaling */}
      <div
        className="fixed pointer-events-none z-30"
        style={{
          left: mousePosition.x,
          top: mousePosition.y,
          width: `${200 + Math.min(velocity.current.x + velocity.current.y, 100)}px`,
          height: `${200 + Math.min(velocity.current.x + velocity.current.y, 100)}px`,
          background: 'radial-gradient(circle, rgba(102, 126, 234, 0.12) 0%, rgba(102, 126, 234, 0.06) 50%, transparent 70%)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          filter: 'blur(12px)',
          willChange: 'transform',
          transition: 'none'
        }}
      />
      
      {/* Ultra-smooth trailing dots with perfect easing */}
      {trail.map((pos, index) => {
        const total = trail.length
        const progress = getTrailPosition(index, total)
        const opacity = progress * 0.9
        const scale = 0.3 + progress * 0.7
        const size = 8 - progress * 4
        const blur = progress * 8
        
        return (
          <div
            key={pos.id}
            className="fixed pointer-events-none z-35"
            style={{
              left: pos.x,
              top: pos.y,
              width: `${size}px`,
              height: `${size}px`,
              background: `radial-gradient(circle, 
                rgba(102, 126, 234, ${opacity}) 0%, 
                rgba(118, 75, 162, ${opacity * 0.6}) 50%,
                rgba(102, 126, 234, ${opacity * 0.3}) 100%
              )`,
              borderRadius: '50%',
              transform: `translate(-50%, -50%) scale(${scale})`,
              boxShadow: `
                0 0 ${15 - progress * 10}px rgba(102, 126, 234, ${opacity * 0.8}),
                0 0 ${30 - progress * 20}px rgba(102, 126, 234, ${opacity * 0.4})
              `,
              filter: `blur(${blur}px)`,
              willChange: 'transform',
              transition: 'none'
            }}
          />
        )
      })}
      
      {/* Velocity-based energy particles */}
      {trail.slice(-12).map((pos, index) => {
        if (index % 2 !== 0) return null
        
        const speed = pos.velocity || 0
        const intensity = Math.min(speed / 10, 1)
        const angle = (index * 30) * (Math.PI / 180)
        const distance = 40 + index * 8
        const particleX = pos.x + Math.cos(angle) * distance
        const particleY = pos.y + Math.sin(angle) * distance
        
        return (
          <div
            key={`particle-${pos.id}-${index}`}
            className="fixed pointer-events-none z-25 sparkle-animation"
            style={{
              left: particleX,
              top: particleY,
              width: `${3 + intensity * 2}px`,
              height: `${3 + intensity * 2}px`,
              background: `radial-gradient(circle, 
                rgba(255, 255, 255, ${0.9 + intensity * 0.1}) 0%, 
                rgba(102, 126, 234, ${0.6 + intensity * 0.4}) 100%
              )`,
              borderRadius: '50%',
              transform: 'translate(-50%, -50%)',
              animationDelay: `${index * 30}ms`,
              animationDuration: `${1 + intensity * 0.5}s`,
              willChange: 'transform'
            }}
          />
        )
      })}
      
      {/* Dynamic background distortion */}
      <div
        className="fixed pointer-events-none z-15"
        style={{
          left: mousePosition.x,
          top: mousePosition.y,
          width: `${300 + Math.min(velocity.current.x + velocity.current.y, 150)}px`,
          height: `${300 + Math.min(velocity.current.x + velocity.current.y, 150)}px`,
          background: `radial-gradient(circle, 
            rgba(102, 126, 234, ${0.06 + Math.min(velocity.current.x + velocity.current.y, 50) / 1000}) 0%, 
            rgba(118, 75, 162, ${0.04 + Math.min(velocity.current.x + velocity.current.y, 50) / 1000}) 50%, 
            transparent 80%
          )`,
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          filter: 'blur(25px)',
          mixBlendMode: 'overlay',
          willChange: 'transform',
          transition: 'none'
        }}
      />
    </>
  )
}

export default CursorFollower
