import React, { useEffect, useState, useRef, useCallback } from 'react'
import { motion, useMotionValue, useSpring, useTransform } from 'framer-motion'

const CursorFollower = () => {
  const [trail, setTrail] = useState([])
  const canvasRef = useRef(null)
  const animationRef = useRef(null)
  const lastMousePosition = useRef({ x: 0, y: 0 })
  const velocity = useRef({ x: 0, y: 0 })

  // Framer Motion values for ultra-smooth animations
  const mouseX = useMotionValue(0)
  const mouseY = useMotionValue(0)
  
  // Spring animations for smooth following - more responsive
  const springConfig = { damping: 20, stiffness: 1000 }
  const cursorX = useSpring(mouseX, springConfig)
  const cursorY = useSpring(mouseY, springConfig)
  
  // Secondary spring for border circle (slightly slower)
  const borderX = useSpring(mouseX, { damping: 25, stiffness: 800 })
  const borderY = useSpring(mouseY, { damping: 25, stiffness: 800 })
  
  // Magnetic field spring
  const magneticX = useSpring(mouseX, { damping: 30, stiffness: 600 })
  const magneticY = useSpring(mouseY, { damping: 30, stiffness: 600 })

  // Velocity-based transforms
  const velocityX = useMotionValue(0)
  const velocityY = useMotionValue(0)
  const velocityMagnitude = useTransform(
    [velocityX, velocityY],
    ([x, y]) => Math.sqrt(x * x + y * y)
  )

  // Smooth mouse tracking with velocity
  const updateMousePosition = useCallback((x, y) => {
    const now = Date.now()
    const deltaTime = now - (lastMousePosition.current.timestamp || now)
    
    if (deltaTime > 0) {
      const vx = (x - lastMousePosition.current.x) / deltaTime * 16
      const vy = (y - lastMousePosition.current.y) / deltaTime * 16
      
      velocity.current.x = vx
      velocity.current.y = vy
      velocityX.set(vx)
      velocityY.set(vy)
    }
    
    lastMousePosition.current = { x, y, timestamp: now }
    mouseX.set(x)
    mouseY.set(y)
  }, [mouseX, mouseY, velocityX, velocityY])

  useEffect(() => {
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    
    // Set canvas size with device pixel ratio for crisp rendering
    const resizeCanvas = () => {
      const dpr = window.devicePixelRatio || 1
      const rect = canvas.getBoundingClientRect()
      
      canvas.width = rect.width * dpr
      canvas.height = rect.height * dpr
      canvas.style.width = rect.width + 'px'
      canvas.style.height = rect.height + 'px'
      
      ctx.scale(dpr, dpr)
    }
    
    resizeCanvas()
    window.addEventListener('resize', resizeCanvas)

    // Optimized mouse move handler
    let rafId = null
    const handleMouseMove = (e) => {
      if (rafId) return
      
      rafId = requestAnimationFrame(() => {
        updateMousePosition(e.clientX, e.clientY)
        
        // Add to trail with smooth interpolation
        setTrail(prev => {
          const newTrail = [...prev, { 
            x: e.clientX, 
            y: e.clientY, 
            id: Date.now(),
            velocity: Math.sqrt(velocity.current.x ** 2 + velocity.current.y ** 2)
          }]
          return newTrail.slice(-20) // Optimized trail length
        })
        
        rafId = null
      })
    }



    window.addEventListener('mousemove', handleMouseMove, { passive: true })

    return () => {
      window.removeEventListener('mousemove', handleMouseMove)
      window.removeEventListener('resize', resizeCanvas)
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current)
      }
      if (rafId) {
        cancelAnimationFrame(rafId)
      }
    }
  }, [updateMousePosition])

  return (
    <>
      {/* High-performance canvas for ripple effects */}
      <canvas
        ref={canvasRef}
        className="fixed top-0 left-0 w-full h-full pointer-events-none z-10"
        style={{ 
          background: 'transparent',
          willChange: 'transform'
        }}
      />
      
      {/* Ultra-smooth main cursor dot with Framer Motion */}
      <motion.div
        className="fixed pointer-events-none z-50"
        style={{
          x: cursorX,
          y: cursorY,
          width: '10px',
          height: '10px',
          background: 'radial-gradient(circle, #667eea 0%, #764ba2 50%, rgba(102, 126, 234, 0.8) 100%)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          boxShadow: `
            0 0 20px rgba(102, 126, 234, 0.8),
            0 0 40px rgba(102, 126, 234, 0.4),
            0 0 60px rgba(102, 126, 234, 0.2)
          `,
          mixBlendMode: 'screen',
          willChange: 'transform'
        }}
      />
      
      {/* Smooth border circle with Framer Motion */}
      <motion.div
        className="fixed pointer-events-none z-40"
        style={{
          x: borderX,
          y: borderY,
          width: '45px',
          height: '45px',
          border: '2px solid rgba(102, 126, 234, 0.7)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          backdropFilter: 'blur(4px)',
          boxShadow: '0 0 40px rgba(102, 126, 234, 0.4)',
          willChange: 'transform'
        }}
      />
      
      {/* Magnetic field with velocity-based scaling */}
      <motion.div
        className="fixed pointer-events-none z-30"
        style={{
          x: magneticX,
          y: magneticY,
          width: useTransform(velocityMagnitude, (v) => `${200 + Math.min(v * 2, 80)}px`),
          height: useTransform(velocityMagnitude, (v) => `${200 + Math.min(v * 2, 80)}px`),
          background: 'radial-gradient(circle, rgba(102, 126, 234, 0.12) 0%, rgba(102, 126, 234, 0.06) 50%, transparent 70%)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          filter: 'blur(12px)',
          willChange: 'transform'
        }}
      />
      
      {/* Ultra-smooth trailing dots with Framer Motion */}
      {trail.map((pos, index) => {
        const total = trail.length
        const progress = index / total
        const opacity = progress * 0.9
        const scale = 0.3 + progress * 0.7
        const size = 8 - progress * 4
        
        return (
          <motion.div
            key={pos.id}
            className="fixed pointer-events-none z-35"
            initial={{ opacity: 0, scale: 0 }}
            animate={{ 
              opacity: opacity,
              scale: scale,
              x: pos.x,
              y: pos.y
            }}
            transition={{ 
              duration: 0.3,
              ease: [0.25, 0.46, 0.45, 0.94]
            }}
            style={{
              width: `${size}px`,
              height: `${size}px`,
              background: `radial-gradient(circle, 
                rgba(102, 126, 234, ${opacity}) 0%, 
                rgba(118, 75, 162, ${opacity * 0.6}) 50%,
                rgba(102, 126, 234, ${opacity * 0.3}) 100%
              )`,
              borderRadius: '50%',
              transform: 'translate(-50%, -50%)',
              boxShadow: `
                0 0 ${15 - progress * 10}px rgba(102, 126, 234, ${opacity * 0.8}),
                0 0 ${30 - progress * 20}px rgba(102, 126, 234, ${opacity * 0.4})
              `,
              willChange: 'transform'
            }}
          />
        )
      })}
      
      {/* Velocity-based energy particles with Framer Motion */}
      {trail.slice(-8).map((pos, index) => {
        if (index % 2 !== 0) return null
        
        const speed = pos.velocity || 0
        const intensity = Math.min(speed / 10, 1)
        const angle = (index * 45) * (Math.PI / 180)
        const distance = 35 + index * 6
        const particleX = pos.x + Math.cos(angle) * distance
        const particleY = pos.y + Math.sin(angle) * distance
        
        return (
          <motion.div
            key={`particle-${pos.id}-${index}`}
            className="fixed pointer-events-none z-25"
            initial={{ opacity: 0, scale: 0 }}
            animate={{ 
              opacity: 1,
              scale: 1,
              x: particleX,
              y: particleY
            }}
            exit={{ opacity: 0, scale: 0 }}
            transition={{ 
              duration: 0.8,
              ease: "easeOut",
              delay: index * 0.05
            }}
            style={{
              width: `${3 + intensity * 2}px`,
              height: `${3 + intensity * 2}px`,
              background: `radial-gradient(circle, 
                rgba(255, 255, 255, ${0.9 + intensity * 0.1}) 0%, 
                rgba(102, 126, 234, ${0.6 + intensity * 0.4}) 100%
              )`,
              borderRadius: '50%',
              transform: 'translate(-50%, -50%)',
              willChange: 'transform'
            }}
          />
        )
      })}
      
      {/* Dynamic background distortion with Framer Motion */}
      <motion.div
        className="fixed pointer-events-none z-15"
        style={{
          x: magneticX,
          y: magneticY,
          width: useTransform(velocityMagnitude, (v) => `${300 + Math.min(v * 3, 120)}px`),
          height: useTransform(velocityMagnitude, (v) => `${300 + Math.min(v * 3, 120)}px`),
          background: `radial-gradient(circle, 
            rgba(102, 126, 234, 0.06) 0%, 
            rgba(118, 75, 162, 0.04) 50%, 
            transparent 80%
          )`,
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          filter: 'blur(25px)',
          mixBlendMode: 'overlay',
          willChange: 'transform'
        }}
      />
    </>
  )
}

export default CursorFollower
