import React, { useEffect, useRef } from 'react'
import { gsap } from 'gsap'

const CursorFollower = () => {
  const cursorRef = useRef(null)
  const borderRef = useRef(null)
  const magneticRef = useRef(null)
  const trailRefs = useRef([])

  useEffect(() => {
    const cursor = cursorRef.current
    const border = borderRef.current
    const magnetic = magneticRef.current

    // Create trail elements
    for (let i = 0; i < 8; i++) {
      const trailDot = document.createElement('div')
      trailDot.style.cssText = `
        position: fixed;
        width: ${8 - i}px;
        height: ${8 - i}px;
        background: radial-gradient(circle, rgba(102, 126, 234, ${0.9 - i * 0.1}) 0%, rgba(118, 75, 162, ${0.6 - i * 0.05}) 100%);
        border-radius: 50%;
        pointer-events: none;
        z-index: 35;
        transform: translate(-50%, -50%);
        box-shadow: 0 0 ${15 - i}px rgba(102, 126, 234, ${0.8 - i * 0.1});
      `
      document.body.appendChild(trailDot)
      trailRefs.current.push(trailDot)
    }

    let mouseX = 0
    let mouseY = 0
    let lastMouseX = 0
    let lastMouseY = 0

    const handleMouseMove = (e) => {
      mouseX = e.clientX
      mouseY = e.clientY
      
      // Calculate velocity
      const velocityX = mouseX - lastMouseX
      const velocityY = mouseY - lastMouseY
      const speed = Math.sqrt(velocityX * velocityX + velocityY * velocityY)
      
      // Animate main cursor
      gsap.to(cursor, {
        x: mouseX,
        y: mouseY,
        duration: 0.1,
        ease: "power2.out"
      })

      // Animate border circle (slightly slower)
      gsap.to(border, {
        x: mouseX,
        y: mouseY,
        duration: 0.2,
        ease: "power2.out"
      })

      // Animate magnetic field
      gsap.to(magnetic, {
        x: mouseX,
        y: mouseY,
        scale: 1 + Math.min(speed / 100, 0.3),
        duration: 0.3,
        ease: "power2.out"
      })

      // Animate trail dots
      trailRefs.current.forEach((dot, index) => {
        gsap.to(dot, {
          x: mouseX,
          y: mouseY,
          duration: 0.1 + index * 0.05,
          ease: "power2.out",
          delay: index * 0.02
        })
      })

      lastMouseX = mouseX
      lastMouseY = mouseY
    }

    window.addEventListener('mousemove', handleMouseMove)

    return () => {
      window.removeEventListener('mousemove', handleMouseMove)
      // Clean up trail elements
      trailRefs.current.forEach(dot => {
        if (dot.parentNode) {
          dot.parentNode.removeChild(dot)
        }
      })
    }
  }, [])

  return (
    <>
      {/* Main cursor dot */}
      <div
        ref={cursorRef}
        className="fixed pointer-events-none z-50"
        style={{
          width: '12px',
          height: '12px',
          background: 'radial-gradient(circle, #667eea 0%, #764ba2 50%, rgba(102, 126, 234, 0.8) 100%)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          boxShadow: `
            0 0 20px rgba(102, 126, 234, 0.8),
            0 0 40px rgba(102, 126, 234, 0.4),
            0 0 60px rgba(102, 126, 234, 0.2)
          `,
          mixBlendMode: 'screen',
          willChange: 'transform'
        }}
      />
      
      {/* Border circle */}
      <div
        ref={borderRef}
        className="fixed pointer-events-none z-40"
        style={{
          width: '50px',
          height: '50px',
          border: '2px solid rgba(102, 126, 234, 0.7)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          backdropFilter: 'blur(4px)',
          boxShadow: '0 0 40px rgba(102, 126, 234, 0.4)',
          willChange: 'transform'
        }}
      />
      
      {/* Magnetic field */}
      <div
        ref={magneticRef}
        className="fixed pointer-events-none z-30"
        style={{
          width: '220px',
          height: '220px',
          background: 'radial-gradient(circle, rgba(102, 126, 234, 0.12) 0%, rgba(102, 126, 234, 0.06) 50%, transparent 70%)',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          filter: 'blur(12px)',
          willChange: 'transform'
        }}
      />
      
      {/* Background distortion */}
      <div
        className="fixed pointer-events-none z-15"
        style={{
          width: '350px',
          height: '350px',
          background: `radial-gradient(circle, 
            rgba(102, 126, 234, 0.06) 0%, 
            rgba(118, 75, 162, 0.04) 50%, 
            transparent 80%
          )`,
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          filter: 'blur(25px)',
          mixBlendMode: 'overlay',
          willChange: 'transform'
        }}
      />
    </>
  )
}

export default CursorFollower
